SELECT        TOP (100) PERCENT dbo.JY67300V.EKOMNR AS Kommunenummer, dbo.JY67300V.EEJDNR AS Ejendomsnummer, dbo.CO11800T.BFE_NUMMER, CASE WHEN CONVERT(VARCHAR, JY67300V.AMATBOG)
                         <> '' THEN RTRIM(CONVERT(VARCHAR, JY67300V.AMATBOG)) + ', ' + JY67700V.LANDSEJERLAVSTEKST END AS Matrikelnummer, dbo.JY67500V.ABELIG AS Ejendomsbeliggenhed,
                         RTRIM(RTRIM(dbo.JY67700V.VEJ_ADRESSENAVN) + ' ' + REPLACE(LTRIM(REPLACE(RTRIM(dbo.JY67700V.HUS_NUMMER), '0', ' ')), ' ', '0')) AS Matrikeladresse, dbo.JY64900V.ANAVN AS Ejers_navn,
                         dbo.JY64900V.ACONAVN AS Ejers_CO_navn, dbo.JY64900V.AADRESS AS Ejers_adresse, dbo.JY64900V.AUDVADR AS Ejers_udvidede_adresse, dbo.JY64900V.APOST AS Postdistrikt,
                         dbo.JY67500V.CBENYT AS Benyttelseskode, CAST(dbo.QL61500V.TEKST AS VARCHAR(70)) AS Benyttelse, dbo.JY64900V.CMSTAT AS Ejers_statuskode, CAST(dbo.QL63200V.TEKST AS VARCHAR(75)) AS Ejerstatus,
                         dbo.JY64900V.CPENS AS Pensionistkode, CAST(dbo.QL63400V.TEKST AS VARCHAR(75)) AS Pensioniststatus, dbo.JY64900V.CLEJE AS Lejeværdikode, CAST(dbo.QL63500V.TEKST AS VARCHAR(75)) AS Lejeværdi,
                         dbo.JY64900V.CBSKADR AS Kode_for_adressebeskyttelse, CAST(dbo.QL63600V.TEKST AS VARCHAR(75)) AS Adressebeskyttelse, dbo.JY64900V.DSLUTS AS Slutseddeldato, dbo.JY64900V.DSKOED AS Skødedato,
                         dbo.JY64900V.DOVTG AS Overtagelsesdato, CAST(dbo.QL63700V.TEKST AS VARCHAR(75)) AS Overdragelsesmåde, dbo.JY64900V.BKOEBS AS Købesum, CAST(CAST(dbo.JY64900V.GKOETAEL AS varchar(6))
                         + '/' + CAST(dbo.JY64900V.GKOENAEV AS varchar(6)) AS varchar(12)) AS Køberandel, CAST(CAST(dbo.JY64900V.GEJTAEL AS varchar(6)) + '/' + CAST(dbo.JY64900V.GEJNAEV AS varchar(6)) AS varchar(12)) AS Ejerandel,
                         dbo.JY64900V.DCPRCIR AS Ejer_CPR_CVR, dbo.JY67700V.VEJ_KODE AS Vejkode, dbo.JY67700V.HUS_NUMMER AS Husnummer_bogstav, dbo.JY67300V.CELAV AS Ejerlavskode, CASE WHEN RTRIM(CONVERT(VARCHAR,
                         JY67700V.MATRIKELNR) + JY67700V.MATRIKELBOGSTAV) <> '' THEN RTRIM(CONVERT(VARCHAR, JY67700V.MATRIKELNR) + JY67700V.MATRIKELBOGSTAV) ELSE RTRIM(JY67300V.AMATBOG) END AS MATNR,
                         CASE WHEN JY67700V.LANDSEJERLAVSTEKST <> '' THEN JY67700V.LANDSEJERLAVSTEKST ELSE JY63500V.CELAVLATXT END AS LANDSEJERLAVSTEKST, dbo.JY67300V.EPCEL AS Parcelnummer,
                         CAST(dbo.QL61000V.TEKST AS VARCHAR(75)) AS Artskode, dbo.JY67300V.FMATAR AS Matrikulært_areal, dbo.JY67300V.FVEJ AS Matrikulært_vejareal, dbo.JY67500V.FTOTAR AS Ejendomsareal,
                         dbo.JY67500V.FTOTVEJ AS Heraf_vejareal, dbo.JY67500V.FARVUR AS Vurderet_areal, dbo.JY67500V.FVEJVUR AS Heraf_vurderet_vejareal, dbo.JY67500V.CEJFORH AS Ejerforholdskode,
                         CAST(dbo.QL50000V.TEKST AS VARCHAR(133)) AS Ejerforhold, dbo.JY67500V.FEJER AS Antal_ejere, dbo.JY67500V.FLEJL AS Antal_lejligheder, dbo.JY67500V.EMODER AS Moderejendomsnummer,
                         dbo.JY67500V.COFVEJ AS Adgang_til_offentlig_vej, dbo.JY67500V.CBSKADRA AS Admin_adressebeskyttelse, dbo.JY67500V.AANAVN AS Administrators_navn, dbo.JY67500V.AACONAVN AS Administrators_CO_navn,
                         dbo.JY67500V.AAADRESS AS Administrators_adresse, dbo.JY67500V.AAUDVADR AS Administrators_udv_adresse, dbo.JY67500V.AAPOST AS Administrators_postdistrikt, dbo.JY67500V.EADMNR AS Administratornummer,
                         dbo.JY67500V.DCPRADM AS Administrators_CPR_CVR, dbo.JY67500V.CADM AS Administratorkode, dbo.QL65000V.TEKST AS Administratorstatus, dbo.JY67300V.EJERLEJLIGHEDSNR, CASE WHEN CONVERT(VARCHAR,
                         JY67700V.KOMMUNENUMMER) <> '' THEN CONVERT(VARCHAR, JY67700V.KOMMUNENUMMER) + RIGHT('0000' + CONVERT(VARCHAR, JY67700V.VEJ_KODE), 4) + RTRIM(JY67700V.HUS_NUMMER) ELSE CONVERT(VARCHAR,
                         JY67500V.EKOMNR) + RIGHT('0000' + CONVERT(VARCHAR, JY67500V.VEJ_KODE), 4) + RTRIM(JY67500V.HUS_NUMMER) END AS ADRESSELINK_ID, CASE WHEN CONVERT(VARCHAR, JY67700V.KOMMUNENUMMER)
                         <> '' THEN CONVERT(VARCHAR, JY67700V.KOMMUNENUMMER) + CONVERT(VARCHAR, JY67700V.VEJ_KODE) + CONVERT(VARCHAR, JY67700V.HUS_NUMMER) ELSE CONVERT(VARCHAR, JY67500V.EKOMNR)
                         + CONVERT(VARCHAR, JY67500V.VEJ_KODE) + RTRIM(JY67500V.HUS_NUMMER) END AS ADRESSE_ID, CASE WHEN CONVERT(VARCHAR, JY67700V.KOMMUNENUMMER) <> '' THEN CONVERT(VARCHAR, CONVERT(VARCHAR,
                         JY67700V.KOMMUNENUMMER) + CONVERT(VARCHAR, JY67700V.VEJ_KODE) + CONVERT(VARCHAR, JY67700V.HUS_NUMMER) + LTRIM(RTRIM(REPLACE(JY67700V.ETAGE, ' ', '')))
                         + LTRIM(RTRIM(REPLACE(JY67700V.SIDE_DOERNR, ' ', '')))) ELSE CONVERT(VARCHAR, JY67500V.EKOMNR) + CONVERT(VARCHAR, JY67500V.VEJ_KODE) + RTRIM(JY67500V.HUS_NUMMER) END AS UDVADRESSEID,

                       CASE WHEN CONVERT(VARCHAR, CO11800T.MATR_NR) <> '' THEN CONVERT(VARCHAR, CO11300T.LANDSEJERLAV_KODE) + '' + CONVERT(VARCHAR, CO11800T.MATR_NR)  + '' +ltrim(CO11800T.MATR_BOGSTAV) END AS MatrikelID, dbo.CO11800T.EJENDOMS_TYPE
FROM            dbo.QL50000V RIGHT OUTER JOIN
                         dbo.QL63500V RIGHT OUTER JOIN
                         dbo.QL63200V RIGHT OUTER JOIN
                         dbo.QL63400V RIGHT OUTER JOIN
                         dbo.QL61500V RIGHT OUTER JOIN
                         dbo.QL65000V RIGHT OUTER JOIN
                         dbo.JY67300V LEFT OUTER JOIN
                         dbo.CO11800T ON dbo.JY67300V.CELAV = dbo.CO11800T.EJERLAV_KODE AND dbo.JY67300V.ABGSTM = dbo.CO11800T.MATR_BOGSTAV AND dbo.JY67300V.EMATNR = dbo.CO11800T.MATR_NR LEFT OUTER JOIN
                         dbo.JY67700V ON dbo.JY67300V.EEJDNR = dbo.JY67700V.EJENDOMSNR AND dbo.JY67300V.ABGSTM = dbo.JY67700V.MATRIKELBOGSTAV AND dbo.JY67300V.EMATNR = dbo.JY67700V.MATRIKELNR AND
                         dbo.JY67300V.CLELAV = dbo.JY67700V.LANDSEJERLAVSKODE LEFT OUTER JOIN
                         dbo.JY67500V ON dbo.JY67300V.EEJDNR = dbo.JY67500V.EEJDNR LEFT OUTER JOIN
                         dbo.JY63500V ON dbo.JY67500V.CELAV = dbo.JY63500V.CELAV ON dbo.QL65000V.KODE = dbo.JY67500V.CADM LEFT OUTER JOIN
                         dbo.QL61000V ON dbo.JY67500V.CARTK = dbo.QL61000V.KODE ON dbo.QL61500V.KODE = dbo.JY67500V.CBENYT LEFT OUTER JOIN
                         dbo.JY64900V ON dbo.JY67300V.EEJDNR = dbo.JY64900V.EEJDNR LEFT OUTER JOIN
                         dbo.QL63700V ON dbo.JY64900V.COVMD = dbo.QL63700V.KODE ON dbo.QL63400V.KODE = dbo.JY64900V.CPENS ON dbo.QL63200V.KODE = dbo.JY64900V.CMSTAT ON dbo.QL63500V.KODE = dbo.JY64900V.CLEJE ON
                         dbo.QL50000V.KODE = dbo.JY64900V.CEJFORH RIGHT OUTER JOIN
                         dbo.QL63600V ON dbo.JY64900V.CBSKADR = dbo.QL63600V.KODE
WHERE        (dbo.JY67500V.CARTK IN (0, 1, 4, 30)) AND (dbo.JY67500V.CBENYT <> '16')